mapper.select:
select *
  from member
 where status = 'Y'
   and mem_id = #{memId}
   and mem_pwd = #{memPwd}

mapper.update:
		update member
   		   set 
   		   	   mem_name = #{memName}
   		     , email = #{email}
 		 where mem_no = #{memNo}

update shelter
   set
   	   sh_name = #{memName}
	 , sh_phone = #{shPhone}
	 , sh_address = #{shAddress}
	 , sh_about = #{shAbout}
 where mem_no = #{memNo}
